DesignSmell.OCLexpression:
Association.allInstances()->exists(
						a|a.memberEnd->exists(
							e1,e2|e1.lower = 1 and e1.isComposite and e1.type.oclAsType(Class).allParents()->includes(e2.type.oclAsType(Class))
						)
					)

DesignSmell.SelectExpression:
Association.allInstances()->select(
						a|a.memberEnd->exists(
							e1,e2|e1.lower = 1 and e1.isComposite and e1.type.oclAsType(Class).allParents()->includes(e2.type.oclAsType(Class))
						)
					)

DesignSmell.OCLexpression:
Class.allInstances->exists(c|
				let E = c.association.memberEnd->select(type <> c and isComposite = true)
				in E->asSet()->size() > 1 and E->exists(lower = 1)
			)

DesignSmell.SelectExpression:
Class.allInstances->select(c|
				let E = c.association.memberEnd->select(type <> c and isComposite = true)
				in E->asSet()->size() > 1 and E->exists(lower = 1)
			)

DesignSmell.OCLexpression:
Generalization.allInstances()->exists(e|Generalization.allInstances()->exists(g|g<>e and g.specific = e.specific
	and (g.general.allParents()->includes(e.general) or g.general = e.general)
	)
)

DesignSmell.SelectExpression:
  Generalization.allInstances()->SELECT(e|Generalization.allInstances()->exists(g|g<>e and g.specific = e.specific
		and (g.general.allParents()->includes(e.general) or g.general = e.general)
		)
	)
